Template.BasicInfo.onCreated(function(){
	var self = this;
	self.autorun(function (){
		self.subscribe('profile');
	});
});

Template.BasicInfo.helpers({
	profile: ()=> {
		return Profile.find({});
	}
});

Template.BasicInfo.helpers({
	empty: ()=> {
		return Recipes.find({"column":1, "category":"Category One","inMenu": true}, {sort: {"position": 1}});
	}
});

Template.BasicInfo.events({
	'click .edit-profile': () => {
		Session.set('editProfile', true);
	}
});
