Template.WebMenu.onCreated(function(){
	var self = this;
	self.autorun(function (){
		self.subscribe('recipes');
	});
});

Template.HomeLayout.helpers({
	recipes: ()=> {
		return Recipes.find({});
	}
});
// Category One
Template.WebMenu.helpers({
	cat1col1: ()=> {
		return Recipes.find({"column":1, "category":"Island Cuisine","inMenu": true}, {sort: {"position": 1}});
	}
});
Template.WebMenu.helpers({
	cat1col2: ()=> {
		return Recipes.find({"column":2, "category":"Island Cuisine","inMenu": true}, {sort: {"position": 1}});
	}
});
Template.WebMenu.helpers({
	cat1col3: ()=> {
		return Recipes.find({"column":3, "category":"Island Cuisine","inMenu": true}, {sort: {"position": 1}});
	}
});
// Category 2
Template.WebMenu.helpers({
	cat2col1: ()=> {
		return Recipes.find({"column":1, "category":"Caribbean Specialties","inMenu": true}, {sort: {"position": 1}});
	}
});
Template.WebMenu.helpers({
	cat2col2: ()=> {
		return Recipes.find({"column":2, "category":"Caribbean Specialties","inMenu": true}, {sort: {"position": 1}});
	}
});
Template.WebMenu.helpers({
	cat2col3: ()=> {
		return Recipes.find({"column":3, "category":"Caribbean Specialties","inMenu": true}, {sort: {"position": 1}});
	}
});

Template.WebMenu.events({
	'click .dropdown-toggle': function (e) {
		e.preventDefault();
		$(e.target).find('.dropdown-menu').toggle();
		}
	});
