ProfileSchema =  new SimpleSchema({
    businessName: {
        type: String,
        label: "Business Name"
    },
    desc: {
        type: String,
        label: "About",
        autoform: {
            rows: 5
         }
    },
    phoneNumber: {
        type: Number,
        min: 9,
        max: 10,
        autoform: {
          afFieldInput: {
            type: "tel"
          }
        }
    },
    author: {
        type: String,
        label: "Author",
        autoValue: function() {
            return this.userId;
        },
        autoform: {
            type: "hidden"
        }
    },
    createdAt: {
        type: Date,
        label: "Created At",
        autoValue: function() {
            return new Date();
        },
        autoform: {
            type: "hidden"
        }

    }
});


Meteor.methods({
	deleteProfile: function(id){
		Profile.remove(id);
	}
});
Profile.attachSchema(ProfileSchema);
